import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class RecipesService {
  private recipes =  [
    {
      id: 'r1',
      title: 'Pizza',
      imageUrl: 'data:image/jpeg;base64,',
      ingredients: ['Capsicum', 'Onion', 'Chilli']
    },
    {
      id: 'r2',
      title: 'Burger',
      imageUrl: 'data:image/jpeg;base64,',
      ingredients: ['French Fries', 'Cold Drink', 'Chilli']
    }
  ]
  constructor() { }

  getAllRecipes() {
    return [...this.recipes];
  }

  getRecipe(id: string) {
    return {...this.recipes.find((recipe) => recipe.id == id)};
  }
}
